import { useContext, useState } from "react";
import { FileExplorerContext } from "./context";
import Input from "./Input";

export default function FileExp({ id = 1 }) {
  const [showChildren, setShowChildren] = useState(false);
  const [showAddInput, setShowAddInput] = useState(false);
  const [showEditInput, setShowEditInput] = useState(false);
  const { nodes, deleteNode, addNode, editNode } =
    useContext(FileExplorerContext);

  const handleClick = () => {
    setShowChildren(!showChildren);
  };

  console.log(nodes);
  return (
    <div className="container">
      <h5>
        {nodes[id].type === "folder" ? (showChildren ? "üìÇ" : "üìÅ") : "üìÑ"}

        {showEditInput ? (
          <Input
            name={nodes[id].name}
            cancel={() => setShowEditInput(false)}
            id={id}
            submit={editNode}
          />
        ) : (
          <>
            <span onClick={handleClick}>{nodes[id].name}</span>

            {nodes[id].type === "folder" && (
              <span onClick={() => setShowAddInput(true)}>‚ûï</span>
            )}
            <span onClick={() => setShowEditInput(true)}>üñäÔ∏è</span>
            <span onClick={() => deleteNode(id)}>‚ùå</span>
          </>
        )}
      </h5>
      <>
        {showAddInput && (
          <Input
            submit={addNode}
            id={id}
            cancel={() => setShowAddInput(false)}
          />
        )}
      </>
      {showChildren &&
        nodes[id]?.children?.map((childId, index) => {
          return <FileExplorer key={index} id={childId} />;
        })}
    </div>
  );
}
