import { useState } from "react";
import data1 from "../../data/data.json"
import "./carousel.css"

function Carousel() {
    const dataLen = data1.length;
    const [datas, setDatas]= useState(28);
    function handleback(){if(datas == 0){
            setDatas(dataLen - 1);
        } else {
            setDatas(datas - 1);
        }
}
    function handleFront(){
        if(datas == dataLen-1){
            setDatas(0);
        } else {
            setDatas(datas + 1);
        }

    }

    
  return (
    <div>
        <div className="container1">
            <div className="backbtn" onClick={handleback}>{"<"}</div>
            <img src={data1[datas].download_url} alt="cc" className="imgc" />
            <div className="frontbtn" onClick={handleFront}>{">"}</div>
        </div>
    </div>
  )
}
export default Carousel;
